import React, { Component } from 'react';
import SideDrawer from './components/Navigation/SideDrawer/SideDrawer';
import Backdrop from './components/Navigation/Backdrop/Backdrop';
import Toolbar from './components/Navigation/Toolbar/Toolbar';
import './App.css';
import {
  Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import Home from './src/components/Pages/Home/Home/Home';
import Coaching from './src/components/Pages/Home/Coaching/Coaching';
import Facility from './src/components/Pages/Home/Facility/Facility';
import Contact from './src/components/Pages/Home/Contact/Contact';

class App extends Component {
  state = {
    SideDrawerOpen: false
  };

  drawerToggleClickHandler = () => {
    this.setState((prevState) => {
      return {SideDrawerOpen: !prevState.SideDrawerOpen};
    });
  };

  backdropClickHandler = () => {
    this.setState({SideDrawerOpen: false})
  };

  render() {
    let backdrop;

    if (this.state.SideDrawerOpen) {
      backdrop = <Backdrop click={this.backdropClickHandler}/>
    }

    return (
      <div style={{height: '100%'}} className="App">
        <Toolbar drawerToggleClickHandler={this.drawerToggleClickHandler}/>
        <SideDrawer show={this.state.SideDrawerOpen}/>
        {backdrop}
        <main style={{ marginTop: '64px'}}>
        <p>This is the page content</p>
        <main>
      <Switch>
        <Route exact path='/' component={Home}/>
        <Route path='/Facility' component={Facility}/>
        {/* <Route path='/Coaching' component={Coaching}/> */}
        <Route path='/Contact' component={Contact}/>
      </Switch>
      </main>

        </main>
      </div>
    );
  }
}

export default App;
